import React, { useMemo, useState } from "react";
import EventsHero from "../components/Alumni-Events/EventsHero.jsx";
import EventCard from "../components/Alumni-Events/EventCard.jsx";
import CalendarView from "../components/Alumni-Events/CalendarView.jsx";
import CreateEventModal from "../components/Alumni-Events/CreateEventModal.jsx";
import SearchFilter from "../components/Alumni-Events/SearchFilter.jsx";
import AddTaskModal from "../components/Alumni-Events/AddTaskModal.jsx";

// Dummy data for events - this would eventually come from your backend
const initialEvents = [
  { id: 1, title: "Future of AI & Technology Conference", description: "Join industry leaders for insights into AI, ML innovations.", date: "2025-10-25", time: "9:00 AM", location: "Tech Convention Center, San Francisco", attendees: 250, registrationOpen: true, category: "tech",imageSrc:"data:image/jpeg;base64," },
  { id: 2, title: "Digital Marketing Summit 2025", description: "Explore the latest trends in digital marketing.", date: "2025-11-12", time: "10:00 AM", location: "Business Hub, New York", attendees: 180, registrationOpen: true, category: "marketing",imageSrc:"data:image/jpeg;base64," },
  { id: 3, title: "UX/UI Design Workshop", description: "Hands-on workshop covering design principles.", date: "2025-09-10", time: "1:00 PM", location: "Design Studio, Austin", attendees: 45, registrationOpen: false, category: "design",imageSrc:"data:image/jpeg;base64," },
  { id: 4, title: "Networking Mixer for Alumni", description: "An informal gathering to connect with fellow alumni.", date: "2025-09-25", time: "6:30 PM", location: "Grand Hotel, New York", attendees: 200, registrationOpen: true, category: "networking",imageSrc:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSC0-I8clejbesIRZSje7rsB5G0Xdj2B_UpsA&s" },
];

export default function EventsPage() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [selectedDateFilter, setSelectedDateFilter] = useState("all");
  const [activeView, setActiveView] = useState("events");
  const [currentPage, setCurrentPage] = useState(1);
  const [tasks, setTasks] = useState([]);
  const [isTaskModalOpen, setIsTaskModalOpen] = useState(false);
  const [clickedDate, setClickedDate] = useState(null);
  const eventsPerPage = 4;

  React.useEffect(() => {
    setCurrentPage(1);
  }, [searchQuery, selectedCategory, selectedDateFilter]);

  const handleDateClick = (arg) => {
    setClickedDate(arg.date);
    setIsTaskModalOpen(true);
  };

  const handleAddTask = (newTask) => {
    setTasks((prev) => [...prev, { ...newTask, id: Date.now().toString() }]);
  };

  const filteredEvents = useMemo(() => {
    return initialEvents.filter((event) => {
      const matchesSearch = event.title.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesCategory = selectedCategory === "all" || event.category === selectedCategory;
      const matchesDate = selectedDateFilter === "all" || (selectedDateFilter === "upcoming" && new Date(event.date) >= new Date()) || (selectedDateFilter === "past" && new Date(event.date) < new Date());
      return matchesSearch && matchesCategory && matchesDate;
    });
  }, [searchQuery, selectedCategory, selectedDateFilter]);

  const totalPages = Math.ceil(filteredEvents.length / eventsPerPage);
  const currentEvents = filteredEvents.slice((currentPage - 1) * eventsPerPage, currentPage * eventsPerPage);

  return (
    <>
      {/* Hero Section */}
      <EventsHero />

      {/* Main Content Section */}
      <section className="bg-gray-50">
        <div className="mx-auto max-w-4xl space-y-8 px-4 py-12">
          {/* Top Control Bar */}
          <div className="flex flex-col items-start justify-between gap-4 sm:flex-row sm:items-center">
            <div className="inline-flex rounded-lg border border-gray-200 bg-white p-1">
              <button onClick={() => setActiveView("events")} className={`rounded-md px-4 py-2 text-sm font-medium transition-colors ${activeView === "events" ? "bg-blue-900 text-white" : " hover:bg-blue-900 hover:text-white"}`}>Upcoming Events</button>
              <button onClick={() => setActiveView("calendar")} className={`rounded-md px-4 py-2 text-sm font-medium transition-colors ${activeView === "calendar" ? "bg-blue-900 text-white" : " hover:bg-blue-900 hover:text-white "}`}>Event Calendar</button>
            </div>  
            <CreateEventModal />
          </div>

          {/* Conditional rendering for Events View vs Calendar View */}
          {activeView === "events" ? (
            <div className="space-y-6">
              <SearchFilter 
                searchQuery={searchQuery}
                onSearchChange={setSearchQuery}
                selectedCategory={selectedCategory}
                onCategoryChange={setSelectedCategory}
                selectedDate={selectedDateFilter}
                onDateChange={setSelectedDateFilter}
              />
              <span className="inline-block rounded-full bg-indigo-100 px-3 py-1 text-sm font-semibold text-primary">
                {filteredEvents.length} events found
              </span>
              {currentEvents.length > 0 ? (
                <div className="space-y-6">
                  {currentEvents.map((event) => <EventCard key={event.id} {...event} />)}
                </div>
              ) : (
                <div className="py-16 text-center">
                  <p className="text-lg font-semibold text-gray-800">No events found</p>
                  <p className="text-gray-500">Try adjusting your search or filters.</p>
                </div>
              )}
              {totalPages > 1 && (
                <div className="flex items-center justify-center gap-2 pt-4">
                  <button onClick={() => setCurrentPage(p => p - 1)} disabled={currentPage === 1} className="rounded-md bg-white px-3 py-1 text-sm shadow-sm hover:bg-gray-100 disabled:opacity-50">Previous</button>
                  <span className="font-medium text-gray-700">Page {currentPage} of {totalPages}</span>
                  <button onClick={() => setCurrentPage(p => p + 1)} disabled={currentPage === totalPages} className="rounded-md bg-white px-3 py-1 text-sm shadow-sm hover:bg-gray-100 disabled:opacity-50">Next</button>
                </div>
              )}
            </div>
          ) : (
            <CalendarView tasks={tasks} onDateClick={handleDateClick} onAddTask={handleAddTask} />
          )}
        </div>
      </section>
      
      <AddTaskModal open={isTaskModalOpen} onOpenChange={setIsTaskModalOpen} selectedDate={clickedDate} onAddTask={handleAddTask} />
    </>
  );
}
